    <div id=""myModal"" class=""modal"">
        <!-- Modal İçeriği -->
        <div class=""modal-content"">
            <span class=""close"">&times;</span>
            <h2>Merhaba!</h2>
            <p>Bu bir jQuery ile yapılmış modal penceredir.</p>
        </div>
    </div>

</div>

<script>
    $(document).ready(function() {
        // Aç butonuna basınca modalı göster
        $(""#openModal"").click(function() {
            $(""#myModal"").fadeIn(); // Animasyonla açılır
        });

        // X butonuna basınca kapat
        $("".close"").click(function() {
            $(""#myModal"").fadeOut(); // Animasyonla kapanır
        });

        // Arka plana tıklayınca da modal kapanır
        $(window).click(function(event) {
            if ($(event.target).is(""#myModal"")) {
                $(""#myModal"").fadeOut();
            }
        });
    });
</scri");
